  `parent_id` int(11) DEFAULT NULL COMMENT 'Khoá ngoại tham chiếu khoá chính bảng product_type cột id, Gọi là "cha"  (ví dụ Điện thoại > IPhone > IPhone 13) thì Điện thoại là "cha" của IPhone, IPhone là "con" của điện thoại và đồng thời Iphone là "cha" của Iphone 13,Iphone 13 là "con" của Iphone, Điện thoại nằm ở đầu list menu nên dc gọi là "cụ tổ" (root),Menu đa cấp.',
  `name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL COMMENT 'Tên menu',
  `img_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Đường dẫn icon hiển thị',
  `is_active` int(11) NOT NULL DEFAULT '1',
  `is_delete` int(11) NOT NULL DEFAULT '0' COMMENT '1: Đã xoá, 0: Chưa xoá (nếu đã xoá thì toàn bộ "con" của nó sẽ bị xoá theo)',
  `created_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'Thời điểm dòng dữ liệu này insert vào bảng',
  PRIMARY KEY (`id`),
  KEY `parent_id` (`parent_id`)
) ENGINE=InnoDB AUTO_INCREMENT=106 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

