DROP TABLE IF EXISTS `user`;
CREATE TABLE IF NOT EXISTS `user` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'Khoá chính tăng tự động để phân biệt các dòng',
  `type` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL COMMENT 'admin | officer | shipper | customer',
  `full_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL COMMENT 'Tên đầy đủ',
  `email` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL COMMENT 'Email liên hệ',
  `phone` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL COMMENT 'Số điện thoại',
  `address` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Địa chỉ',
  `birthday` datetime NOT NULL COMMENT 'Ngày sinh',
  `img_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'lưu đường dẫn ảnh đại diện',
  `password` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL COMMENT 'Mật khẩu',
  `paging` int(11) DEFAULT '5' COMMENT 'Phân trang (cấu hình phân trang cho mỗi bảng quản lý)',
  `created_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'Thời điểm insert dòng dữ liệu này vào bảng',
  `is_lock` int(11) NOT NULL DEFAULT '0' COMMENT 'Mang 2 giá trị : 0: Chưa khoá ng dùng, 1: đã khoá ng dùng',
  `is_delete` int(11) NOT NULL DEFAULT '0' COMMENT 'Đã xoá (1: xoá rồi,0: Chưa xoá)',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=251 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

